mapper.select:
select * from dts_reserve

    1=1

        and scene = #{scene,jdbcType=INTEGER}


        and table_number = #{tableNumber,jdbcType=VARCHAR}



            and DATE_FORMAT(start_time, '%Y-%m-%d') = #{date,jdbcType=VARCHAR}


            and (start_time > #{startTime,jdbcType=TIMESTAMP} and start_time < #{endTime,jdbcType=TIMESTAMP})
            or  (end_time > #{startTime,jdbcType=TIMESTAMP} and end_time < #{endTime,jdbcType=TIMESTAMP})
